import React, { Fragment, useState } from 'react';
import Message from './Message';
import Progress from './Progress';
import axios from 'axios';

const FileUpload = () => {
  const [file, setFile] = useState('');
  const [filename, setFilename] = useState('Choose File');
  const [uploadedFile, setUploadedFile] = useState({});
  const [message, setMessage] = useState('');
  const [uploadPercentage, setUploadPercentage] = useState(0);
  const [show, setShow] = useState(false);
  const [users, setUser] = useState([{}]);
  const [artists] = useState([]);
  const onChange = e => {
    setFile(e.target.files[0]);
    setFilename(e.target.files[0].name);
  };


  const app =(e)=>{
    window.alert(e.target.name);
    artists.push({
      name: e.target.name,
    });
    e.target.className="btn btn-light";
    console.log(artists);
    
  }

  const insColumn=async (e)=>{

    const res= await fetch('/inscsv' , {
      method:"POST",
      headers:{
        "Content-Type":"application/json"
      },
      body:JSON.stringify({
        users,artists
      })
    });
    const data = await res.json();
    if ('message' in data)
    {
      window.alert('Registration Successfully');
      // navigate("/SignIn");
    }
    else 
    {
      window.alert(data["error"]);
    }



  }
  const onSubmit = async e => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('file', file);

    try {
      const res = await axios.post('/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        },
        onUploadProgress: progressEvent => {
          setUploadPercentage(
            parseInt(
              Math.round((progressEvent.loaded * 100) / progressEvent.total)
            )
          );
        }
      });
      
      // Clear percentage
      setTimeout(async() => setUploadPercentage(0), 10000);
      const { fileName, filePath ,cc} = await res.data;

      await setUser(cc);
      setShow(true);

    
     
      
      await setUploadedFile({ fileName, filePath });
        
      setMessage('File Uploaded');
    } catch (err) {
      if (err.response.status === 500) {
        setMessage('There was a problem with the server');
      } else {
        setMessage(err.response.data.msg);
      }
      setUploadPercentage(0)
    }
  };

  return (
    <>
    <Fragment>
      {message ? <Message msg={message} /> : null}
      <form onSubmit={onSubmit}>
        <div className='custom-file mb-4'>
          <input
            type='file'
            className='custom-file-input'
            id='customFile'
            onChange={onChange}
            />
          <label className='custom-file-label' htmlFor='customFile'>
            {filename}
          </label>
        </div>

        <Progress percentage={uploadPercentage} />

        <input
          type='submit'
          value='Upload'
          className='btn btn-primary btn-block mt-4'
          />
      </form>
      {show && (
        <div className='row mt-5'>
          <div className='col-md-6 m-auto'>
            <h3 className='text-center'>Pleace select column Name</h3>
          </div>
        </div>
      )}
    </Fragment>
    <center>
    <h1>
                {show && <h1>

                    {
                        Object.keys(users[0]).map((key, i) => (
                            
                            <>
                            

                            <button onClick={app} name={key} className='btn btn-primary btn-block ' style={{"margin-left": "40px"}}>{key}</button>
                           
                          
                                  </>
                        ))
                    }


                </h1>
                }

                <button onClick={insColumn}>submit</button>
            </h1>
            </center>
          </>

  );
};

export default FileUpload;
